import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './leftbar.css';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu'; // 确保已安装@material-ui/icons
import logo from '../../assets/logo.png';

function LeftBar(props) {
  const [selectedIndex, setSelectedIndex] = useState(props.tabNav);
  const [isNavVisible, setNavVisible] = useState(true); // 控制导航栏的显示状态，默认为显示

  useEffect(() => {
    setSelectedIndex(props.tabNav);
  }, [props.tabNav]);

  const toggleNav = () => {
    setNavVisible(!isNavVisible);
  };

  return (
    <div id={isNavVisible ? 'leftbar' : 'leftbar-collapsed'}>
      <IconButton onClick={toggleNav} className="menu-button" style={{ color: 'white' }}>
        <MenuIcon />
      </IconButton>
      <Card variant="outlined" className='topbox'>
        <CardContent>
          <img className='logo' src={logo} alt="logo" />
          <Typography className='details' gutterBottom>
            Student Name: xxx
          </Typography>
          <Typography className='details' gutterBottom>
            Student ID: xxx
          </Typography>
          <Typography className='details'>
            Year: xxx
          </Typography>
        </CardContent>
      </Card>
      <Divider />
      <List className='navbox'>
        {['Programme', 'Modules', 'CourseWork', 'Exam', 'Absence', 'MyTutor', 'MyPrograme'].map((item) => (
          <React.Fragment key={item}>
            <ListItem button component={Link} className={selectedIndex === item ? 'sure' : ''} to={`/${item}`}>
              <i className={`iconfont icon-${item.toLowerCase()}`}></i>
              {item}
            </ListItem>
            <Divider />
          </React.Fragment>
        ))}
      </List>
    </div>
  );
}

export default LeftBar;
