import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LeftBar from './../../component/leftbar/leftbar';
import './coursework.css';
import { Button, TextField, Divider } from '@material-ui/core';
import { moduleName, courseworkTime, moduleIdList, courseworkMark, uploadCoursework } from '../../api/api';

function CourseWork() {
  const [modules, setModules] = useState([]);
  const [currentModuleIndex, setCurrentModuleIndex] = useState(0);
  const [file, setFile] = useState(null);
  const [text, setText] = useState('');
  const [dataLoaded, setDataLoaded] = useState(false); // 追踪数据加载状态

  useEffect(() => {
    axios.get(moduleIdList)
      .then(idResponse => {
        if (idResponse.data.code === 200) {
          const fetchedModuleIds = idResponse.data.obj.map(id => ({ moduleId: id }));
          axios.get(moduleName)
            .then(nameResponse => {
              if (nameResponse.data.code === 200) {
                const combinedModules = fetchedModuleIds.map((mod, index) => ({
                  ...mod,
                  moduleName: nameResponse.data.obj[index]
                }));
                return axios.get(courseworkTime)
                  .then(timeResponse => {
                    if (timeResponse.data.code === 200) {
                      const times = timeResponse.data.obj;
                      combinedModules.forEach((mod, index) => {
                        mod.startTime = times[index] ? times[index].releaseTime : '暂无数据'; // 检查是否有数据，否则设置默认值
                        mod.endTime = times[index] ? times[index].endTime : '暂无数据'; // 检查是否有数据，否则设置默认值
                      });
                      return axios.get(courseworkMark)
                        .then(markResponse => {
                          if (markResponse.data.code === 200) {
                            const marks = markResponse.data.obj;
                            combinedModules.forEach((mod, index) => {
                              mod.mark = marks[index] ? marks[index] : '暂无数据'; // 检查是否有数据，否则设置默认值
                            });
                            setModules(combinedModules);
                            setDataLoaded(true); // 数据加载成功
                          }
                        });
                    }
                  })
              }
            });
        }
      })
      .catch(error => {
        console.error('获取模块数据时出错:', error);
        setDataLoaded(false); // 数据加载失败
      });
  }, []);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/zip') {
      setFile(selectedFile);
    } else {
      alert('请上传zip压缩包文件');
    }
  };

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = () => {
    const formData = new FormData();
    formData.append('file', file);
    formData.append('text', text);

    axios.post(uploadCoursework, formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
    .then(response => {
      if(response.data.code === 200) {
        alert('作业提交成功！');
      } else {
        throw new Error('提交错误');
      }
    })
    .catch(error => {
      console.error('提交失败:', error);
      alert('作业提交失败，请重试！');
    });
  };

  return (
    <div className='container' id='coursework'>
      <div className='leftbox'>
        <LeftBar tabNav="CourseWork" />
      </div>
      <div className='maincenter'>
        <div className='topline'>
          <div className='topMain'>
            <h2 className='gFont'>模块</h2>
            <div className='fn-clear'>
              {modules.map((module, index) => (
                <Button
                  key={module.moduleId}
                  size="small"
                  variant="contained"
                  color="primary"
                  onClick={() => setCurrentModuleIndex(index)}
                >
                  ID:{module.moduleId}
                </Button>
              ))}
            </div>
          </div>
        </div>
        <Divider />
        <div className='centerbox'>
          {modules.length > 0 && dataLoaded && ( // 只有在数据加载成功后才显示模块信息
            <div className='btn-group'>
              <div className='leftbox'>
                <h2 className='gFont'>{modules[currentModuleIndex].moduleName}</h2>
                <p>
                  开始时间: {modules[currentModuleIndex].startTime}，
                  结束时间: {modules[currentModuleIndex].endTime}
                </p>
                <p>
                  分数: {modules[currentModuleIndex].mark}
                </p>
              </div>
              <div className='righttxt'>
                <h2 className='gFont'>作业详情</h2>
                <div className='txt'>
                  作业详情: xxxxxxxxxxxxxxxxxxx
                </div>
              </div>
            </div>
          )}
          {!dataLoaded && ( // 当数据加载失败时显示提示信息
            <div className='loading-msg'>
              <p>数据加载失败，请稍后重试。</p>
            </div>
          )}
        </div>
        <div className='subform' style={{ width: "800px" }}>
          <p className='line'>
            <span>提交文件: </span>
            <input type="file" accept=".zip" onChange={handleFileChange} />
          </p>
          <br />
          <TextField
            label="请输入内容"
            variant="outlined"
            fullWidth
            type='text'
            multiline
            minRows={4}
            style={{ margin: 8 }}
            value={text}
            onChange={handleTextChange}
          />
          <br />
          <Button variant="contained" color="secondary" onClick={handleSubmit}>
            提交
          </Button>
        </div>
      </div>
    </div>
  );
}

export default CourseWork;
