import React, { useState, useEffect, Fragment, useRef } from 'react';
import axios from 'axios';
import LeftBar from './../../component/leftbar/leftbar';
import { useNavigate } from 'react-router-dom';
import './Modules.css';
import {
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper,
  Button, IconButton, Collapse, Box, Divider, Typography
} from '@material-ui/core';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import { moduleIdInfo, moduleIdCredits, moduleOverview } from '../../api/api';

function Modules() {
  const [list, setList] = useState([]);
  const [loading, setLoading] = useState(true);  // Added to track loading state
  const [error, setError] = useState(false);     // Added to track error state
  const navigate = useNavigate();
  const refsdata = useRef([]);
  const tableBoxRef = useRef(null);

  useEffect(() => {
    // Fetch module IDs and names
    axios.get(moduleIdInfo)
      .then(response => {
        if (response.data.code === 200) {
          const modulesData = response.data.obj.map(module => ({
            moduleId: module.moduleId,
            moduleName: module.moduleName,
          }));
          fetchModuleCredits(modulesData);
        } else {
          throw new Error('Failed to fetch module IDs');
        }
      })
      .catch(error => {
        console.error('Error fetching module IDs:', error);
        setError(true);
        setLoading(false);
      });
  }, []);

  const fetchModuleCredits = (modulesData) => {
    axios.get(moduleIdCredits)
      .then(response => {
        if (response.data.code === 200) {
          const creditsData = response.data.obj;
          const updatedModules = modulesData.map(module => ({
            ...module,
            credits: creditsData.find(credit => credit.moduleName === module.moduleName)?.credits || 'N/A',
          }));
          fetchModuleOverviews(updatedModules);
        } else {
          throw new Error('Failed to fetch module credits');
        }
      })
      .catch(error => {
        console.error('Error fetching module credits:', error);
        setError(true);
        setLoading(false);
      });
  };

  const fetchModuleOverviews = (modulesData) => {
    axios.get(moduleOverview)
      .then(response => {
        if (response.data.code === 200) {
          const overviewData = response.data.obj;
          const updatedModules = modulesData.map(module => ({
            ...module,
            overview: overviewData.find(overview => overview.includes(module.moduleName)) || 'No overview available',
          }));
          setList(updatedModules);
        } else {
          throw new Error('Failed to fetch module overviews');
        }
      })
      .catch(error => {
        console.error('Error fetching module overview:', error);
        setError(true);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  return (
    <div className='container' id='Modules'>
      <div className='leftbox'>
        <LeftBar tabNav="Modules" />
      </div>
      <div className='maincenter'>
        <div className='topline'>
          <div className='topMain'>
            <h2 className='gFont'>Module</h2>
            <div className='fn-clear'>
              {list.map((ele, i) => (
                <Button
                  key={i}
                  size="small"
                  variant="contained"
                  color="primary"
                  onClick={() => { handleButtonClick(i); }}
                >
                  ID:{ele.moduleId}
                </Button>
              ))}
            </div>
          </div>
        </div>
        <div className='mainList'>
          <h2 className='gFont'>Module List</h2>
          {error ? (
            <Typography color="error">Failed to load data. Please try again later.</Typography>
          ) : (
            <TableContainer component={Paper} className='tablebox' ref={tableBoxRef}>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>Module</TableCell>
                  <TableCell align="center">Credits</TableCell>
                  <TableCell align="center"></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {loading ? (
                  <TableRow>
                    <TableCell colSpan={3} align="center">
                      Loading data...
                    </TableCell>
                  </TableRow>
                ) : error ? (
                  <TableRow>
                    <TableCell colSpan={3} align="center">
                      Failed to load data. Please try again later.
                    </TableCell>
                  </TableRow>
                ) : (
                  list.map((item, i) => (
                    <Fragment key={i}>
                      <TableRow id={`line${i}`}>
                        <TableCell component="th" scope="row" ref={el => refsdata.current[i] = el}>
                          {item.moduleName}
                        </TableCell>
                        <TableCell align="center">{item.credits}</TableCell>
                        <TableCell align="center">
                          <IconButton aria-label="expand row" size="small" onClick={() => {
                            let updatedList = [...list];
                            updatedList[i].open = !updatedList[i].open;
                            setList(updatedList);
                          }}>
                            {item.open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                          </IconButton>
                        </TableCell>
                      </TableRow>
                      <TableRow>
                        <TableCell colSpan={3}>
                          <Collapse in={item.open} timeout="auto" unmountOnExit>
                            <Box margin={1}>
                              <Typography variant="subtitle1" display="block">
                                Overview
                              </Typography>
                              <Typography variant="body1" display="block">
                                {item.overview}
                              </Typography>
                              <Divider style={{ margin: '32px 0' }} />
                            </Box>
                          </Collapse>
                        </TableCell>
                      </TableRow>
                    </Fragment>
                  ))
                )}
              </TableBody>
            </Table>
          </TableContainer>
          
          )}
        </div>
      </div>
    </div>
  );
}

export default Modules;
