import React, { useState, useEffect } from 'react';
import LeftBar from '../../component/leftbar/leftbar';
import { Button } from '@material-ui/core';
import './absence.css';
import axios from 'axios';
import { absenceDetails } from '../../api/api';

function Absence() {
  // 使用useState跟踪邮件列表和当前选中的邮件索引
  const [emails, setEmails] = useState([]);
  const [currentEmailIndex, setCurrentEmailIndex] = useState(0);

  useEffect(() => {
    // 获取邮件数据的函数
    const fetchEmails = () => {
      axios.get(absenceDetails)  // 替换 YOUR_API_ENDPOINT 为你的 API 端点
        .then(response => {
          if (response.data.code === 200 && response.data.obj) {
            setEmails(prevEmails => [...prevEmails, { moduleId: response.data.obj.moudleId, email: response.data.obj.email }]);
          }
        })
        .catch(error => {
          console.error('获取邮件数据失败:', error);
        });
    };

    fetchEmails();
  }, []);

  return (
      <div className='container' id='absence'>
        <div className='leftbox'>
          <LeftBar tabNav="Absence" />
        </div>
        <div className='maincenter'>
          <div className='topline'>
            <div className='topMain'>
              <h2 className='gFont'>Module</h2>
              <div className='fn-clear'>
                {emails.map((_, i) => (
                  // 遍历邮件列表，生成按钮
                    <Button
                        key={i}
                        size="small"
                        variant="contained"
                        color="primary"
                        onClick={() => setCurrentEmailIndex(i)}
                    >
                      ID:{i + 1}
                    </Button>
                ))}
              </div>
            </div>
          </div>
          <div className='centerbox'>
            <div className='btn-group'>
              <div className='leftbox'>
                <h2 className='gFont'>Email</h2>
                <p className='timesline'>
                  {/* 显示当前选中的邮件，并添加点击事件跳转到邮件应用程序 */}
                  <Button
                      size="small"
                      onClick={() => {
                        window.location.href = `mailto:${emails[currentEmailIndex]}`;
                      }}
                      variant="contained"
                      color="secondary"
                  >
                    {emails[currentEmailIndex]}
                  </Button>
                </p>
              </div>
            </div>
          </div>
          <div className='tipbox'>
            <h2 className='gFont'>Warning</h2>
            <p className='desc'>
              xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx
            </p>
          </div>
        </div>
      </div>
  );
}

export default Absence;
