import React, { useState, useEffect } from 'react';
import LeftBar from '../../component/leftbar/leftbar';
import { Button } from '@material-ui/core';
import './exam.css';
import axios from 'axios';
import { examDetails } from '../../api/api';

function Exam() {
  const [modules, setModules] = useState([]);
  const [currentModule, setCurrentModule] = useState(null);

  useEffect(() => {
    // 定义获取考试模块数据的函数
    const fetchExamDetails = () => {
      axios.get(examDetails)
        .then(response => {
          if (response.data.code === 200) {
            const fetchedModules = response.data.obj.modules; // 假设数据在 response.data.obj.modules 中
            setModules(fetchedModules);
            setCurrentModule(fetchedModules[0]); // 将第一个模块设为当前模块
          }
        })
        .catch(error => {
          console.error('获取考试详情失败:', error);
        });
    };

    // 调用获取考试模块数据的函数
    fetchExamDetails();
  }, []);

  if (!modules.length) return <p>加载中...</p>; // 数据未加载完成时显示

  return (
      <div className='container' id='exam'>
        <div className='leftbox'>
          <LeftBar tabNav="Exam" />
        </div>
        <div className='maincenter'>
          <div className='topline'>
            <div className='topMain'>
              <h2 className='gFont'>Module</h2>
              <div className='fn-clear'>
                {modules.map((module, i) => (
                  <Button
                    key={i}
                    size="small"
                    variant="contained"
                    color="primary"
                    onClick={() => setCurrentModule(module)}
                  >
                    ID:{i + 1}
                  </Button>
                ))}
              </div>
            </div>
          </div>
          <div className='centerbox'>
            <div className='btn-group'>
              <div className='leftbox'>
                <h2 className='gFont'>{currentModule?.moduleName}</h2>
                <p className='timesline'>
                  开始时间：{currentModule?.startTime}
                  <br />
                  结束时间：{currentModule?.endTime}
                  <br />
                  持续时间：{currentModule?.duration}
                </p>
              </div>
              <div className='righttxt'>
                分数：
                <br />
                <span className='gFont'>{currentModule?.mark}</span>
              </div>
            </div>
          </div>
          <div className='tipbox'>
            <h2 className='gFont'>Tips</h2>
            <p className='desc'>
              xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx
            </p>
          </div>
        </div>
      </div>
  );
}

export default Exam;
