import React, { useState } from 'react';
import './App.css';
import { Outlet } from 'react-router-dom';
import LeftBar from '../src/component.'; // 导入 LeftBar 组件

function App() {
  const [isSidebarExpanded, setSidebarExpanded] = useState(true);

  // 定义处理左侧导航栏展开与收起状态变化的函数
  const handleToggleSidebar = (expanded) => {
    setSidebarExpanded(expanded);
  };

  return (
    <div className='mainbox'>
      {/* 将 handleToggleSidebar 函数传递给 LeftBar 组件 */}
      <LeftBar onToggleSidebar={handleToggleSidebar} />
      <Outlet />
    </div>
  );
}

export default App;
