import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu'; // 导入菜单图标
import './leftbar.css';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import logo from '../../assets/logo.png';

function LeftBar(props) {
  const [selectedIndex, setSelectedIndex] = useState(props.tabNav);
  const [isOpen, setIsOpen] = useState(false); // 新增状态控制导航栏开关

  const toggleDrawer = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    console.log(props.tabNav);
    setSelectedIndex(props.tabNav);
  }, [props.tabNav]);

  return (
    <div id='leftbar' style={{ width: isOpen ? '250px' : '0' }}> // 动态设置宽度
      {/* 添加按钮切换导航栏 */}
      <IconButton onClick={toggleDrawer} style={{ color: 'white', marginLeft: '10px' }}>
        <MenuIcon />
      </IconButton>

      {/* 渲染顶部 */}
      <Card variant="outlined" className='topbox' hidden={!isOpen}> // 使用hidden控制显示
        <CardContent>
          <img className='logo' src={logo} alt="logo" />
          <Typography className='details' gutterBottom>
            Student Name: xxx
          </Typography>
          <Typography className='details' gutterBottom>
            Student ID: xxx
          </Typography>
          <Typography className='details'>
            Year: xxx
          </Typography>
        </CardContent>
      </Card>

      {/* 渲染导航栏 */}
      <Divider hidden={!isOpen} />
      <List className='navbox' hidden={!isOpen}>
        <ListItem button component={Link} to="/Programme" className={selectedIndex === 'Programme' ? 'sure' : ''}>
          <i className='iconfont icon-tianjiajihua'></i> Programme
        </ListItem>
        {/* 更多列表项 */}
      </List>
    </div>
  );
}

export default LeftBar;
