import React, { useState, useEffect, Fragment, useRef, createRef } from 'react';
import LeftBar from './../../component/leftbar/leftbar'
import { useNavigate } from 'react-router-dom';
import './Modules.css'
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  IconButton,
  Collapse,
  Box,
  Divider,
} from '@material-ui/core';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import Typography from '@material-ui/core/Typography';

function Modules() {
  const [list, setlist] = useState([
    { id: 1, module: 'Mathematics', credits: 10, open: false },
    { id: 2, module: 'Physics', credits: 20, open: false },
    { id: 3, module: 'History', credits: 10, open: false },
    { id: 4, module: 'Computer Science', credits: 20, open: false },
    // 其他模块数据...
  ]);
  const navigate = useNavigate();
  const refsdata = useRef([]);
  useEffect(() => {
    refsdata.current = list.map((_, i) => refsdata.current[i] ?? createRef());
  }, [list]);
  const tableBoxRef = useRef(null);

  const handleButtonClick = (index) => {
    const updatedList = list.map((item, idx) =>
      idx === index ? { ...item, open: !item.open } : item
    );
    setlist(updatedList);
    navigate(`/Modules#line${index}`);

    setTimeout(() => {
      const rowElement = refsdata.current[index];
      if (rowElement && rowElement.current) {
        tableBoxRef.current.scrollTo({
          top: rowElement.current.offsetTop,
          behavior: 'smooth',
        });
      }
    }, 0);
  };

  return (
    <div className='container' id='Modules'>
      <div className='leftbox'>
        <LeftBar tabNav="Modules" />
      </div>
      <div className='maincenter'>
        <div className='topline'>
          <div className='topMain'>
            <h2 className='gFont'>Module</h2>
            <div className='fn-clear'>
              {list.map((ele, i) => {
                return (
                  <Button
                    key={i}
                    size="small"
                    variant="contained"
                    color="primary"
                    onClick={() => { handleButtonClick(i) }}
                  >
                    ID:{ele.id}
                  </Button>
                );
              })}
            </div>
          </div>
        </div>
        <div className='mainList'>
          <h2 className='gFont'>Module List</h2>
          <TableContainer component={Paper} className='tablebox' ref={tableBoxRef}>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>Module</TableCell>
                  <TableCell align="center">Credits</TableCell>
                  <TableCell align="center"></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {list.map((item, i) => (
                  <Fragment key={i}>
                    <TableRow id={`line${i}`}>
                      <TableCell component="th" scope="row" ref={refsdata.current[i]}>
                        {item.module}
                      </TableCell>
                      <TableCell align="center">{item.credits}</TableCell>
                      <TableCell align="center">
                        <IconButton aria-label="expand row" size="small" onClick={() => {
                          let showlist = list;
                          showlist[i].open = !showlist[i].open;
                          setlist([...showlist])
                        }}>
                          {item.open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                        </IconButton>
                      </TableCell>
                    </TableRow>
                    <TableRow>
                      <TableCell colSpan={3}>
                        <Collapse in={item.open} timeout="auto" unmountOnExit>
                          <Box margin={1}>
                            <Typography variant="subtitle1" display="block">
                              Overview
                            </Typography>
                            <Typography variant="body1" display="block">
                            This module aims to provide an integrated introduction to electronics engineering, taught using challenge and problem-based learning infused with theory, practise, and research.
                            </Typography>
                            <Divider style={{ margin: '32px 0' }} />
                            <Typography variant="subtitle1" display="block">
                              Syllabus
                            </Typography>
                            <Typography variant="body1" display="block" style={{ whiteSpace: 'pre-line' }}>
                                  {`Diodes: structure, working, types and applications\n
                                      Bipolar Junction Transistors (BJT) – structure, characteristics, load line analysis and applications\n
                                        BJT configuration as an amplifier and a switch\n
                                    Field Effect Transistors (Junction FETs, Metal-oxide semiconductor FETs) structure, working and characteristics\n
                                  Silicon Controlled Rectifiers, Thyristors and Triacs, working and applications\n
  Operational amplifiers: basic structure and configuration\n
  Op-amp application as filters, comparators, and oscillators\n
  Circuit design and prototyping\n
  Voltage regulators\n
  Rectifiers and inverter circuits\n
  Power electronic devices and systems\n
  Sensors, transducers, and their applications`}
</Typography>

                          </Box>
                        </Collapse>
                      </TableCell>
                    </TableRow>
                  </Fragment>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </div>
      </div>
    </div>
  );
}

export default Modules;
