import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './leftbar.css';
import { Drawer, IconButton, List, ListItem, Divider, Card, CardContent, Typography } from '@material-ui/core';
import MenuIcon from '@material-ui/icons/Menu'; // 引入菜单图标
import logo from '../../assets/logo.png';

function LeftBar(props) {
    const [selectedIndex, setSelectedIndex] = useState(props.tabNav);
    const [drawerOpen, setDrawerOpen] = useState(false);

    const CustomLink = React.forwardRef((props, ref) => (
        <Link ref={ref} {...props} />
    ));

    useEffect(() => {
        setSelectedIndex(props.tabNav);
    }, [props.tabNav]);

    const toggleDrawer = () => {
        setDrawerOpen(!drawerOpen);
    };

    return (
        <div id='leftbar'>
            <IconButton onClick={toggleDrawer} className="toggle-button">
                <MenuIcon />
            </IconButton>

            <Drawer anchor="left" open={drawerOpen} onClose={toggleDrawer}>
                <Card variant="outlined" className='topbox'>
                    <CardContent>
                        <img className='logo' src={logo} alt="logo" />
                        <Typography className='details' gutterBottom>
                            Student Name:xxx
                        </Typography>
                        <Typography className='details' gutterBottom>
                            Student ID:xxx
                        </Typography>
                        <Typography className='details'>
                            Year:xxx
                        </Typography>
                    </CardContent>
                </Card>

                <Divider />
                <List className='navbox'>
                    {/* 这里保持你的原有列表项和图标 */}
                </List>
            </Drawer>
        </div>
    );
}

export default LeftBar;
