import React, { useState } from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import IconButton from '@material-ui/core/IconButton';
import Divider from '@material-ui/core/Divider';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import { Link } from 'react-router-dom';
import './leftbar.css'; // Ensure this path is correct for your CSS
import logo from '../../assets/logo.png'; // Ensure this path is correct for your logo

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
    whiteSpace: 'nowrap',
  },
  drawerOpen: {
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerClose: {
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    overflowX: 'hidden',
    width: theme.spacing(7) + 1,
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9) + 1,
    },
  },
  toolbar: theme.mixins.toolbar,
  menuButton: {
    position: 'absolute',
    top: 10,
    left: 10,
    color: 'white',
  }
}));

function LeftBar(props) {
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(props.tabNav);

  const handleDrawerToggle = () => {
    setOpen(!open);
  };

  const CustomLink = React.forwardRef((linkProps, ref) => (
    <Link ref={ref} {...linkProps} />
  ));

  return (
    <div className={classes.root} id="leftbar">
      <CssBaseline />
      <Drawer
        variant="permanent"
        className={clsx({
          [classes.drawerOpen]: open,
          [classes.drawerClose]: !open,
        })}
        classes={{
          paper: clsx({
            [classes.drawerOpen]: open,
            [classes.drawerClose]: !open,
          }),
        }}
      >
        <IconButton
          onClick={handleDrawerToggle}
          className={classes.menuButton}
        >
          <i className='iconfont icon-tianjiajihua'></i>
        </IconButton>
        <div className="topbox">
          <img className='logo' src={logo} alt="logo" />
        </div>
        <Divider />
        <List className='navbox'>
          {['Programme', 'Modules', 'CourseWork', 'Exam', 'Absence', 'MyTutor', 'MyProgramme'].map((text, index) => (
            <ListItem
              button
              component={CustomLink}
              className={selectedIndex === text ? 'sure' : ''}
              to={`/${text}`}
              onClick={() => setSelectedIndex(text)}
              key={text}
            >
              <i className={`iconfont icon-${text.toLowerCase()}`}></i>
              {text}
            </ListItem>
          ))}
        </List>
      </Drawer>
    </div>
  );
}

export default LeftBar;
