import React, { useState, useEffect, Fragment, useRef, createRef } from 'react';
import axios from 'axios';
import LeftBar from './../../component/leftbar/leftbar'
import { useNavigate } from 'react-router-dom';
import './Modules.css'
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  IconButton,
  Collapse,
  Box,
  Divider,
} from '@material-ui/core';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import Typography from '@material-ui/core/Typography';

function Modules() {
  const [list, setList] = useState([]);
  const navigate = useNavigate();
  const refsdata = useRef([]);
  useEffect(() => {
    // Fetch module data from backend
    axios.get('http://localhost:3000/api/modules')
      .then(response => {
        setList(response.data);
        // Initialize refsdata
        refsdata.current = response.data.map((_, i) => refsdata.current[i] ?? createRef());
      })
      .catch(error => {
        console.error('Error fetching module data:', error);
      });
  }, []);

  const tableBoxRef = useRef(null);

  const handleButtonClick = (index) => {
    const updatedList = list.map((item, idx) =>
      idx === index ? { ...item, open: !item.open } : item
    );
    setList(updatedList);
    navigate(`/Modules#line${index}`);

    // Fetch overview and syllabus for the clicked module
    const moduleId = list[index].id;
    fetchModuleDetails(moduleId);

    setTimeout(() => {
      const rowElement = refsdata.current[index];
      if (rowElement && rowElement.current) {
        tableBoxRef.current.scrollTo({
          top: rowElement.current.offsetTop,
          behavior: 'smooth',
        });
      }
    }, 0);
  };

  // Function to fetch overview and syllabus for a module
  const fetchModuleDetails = (moduleId) => {
    axios.all([
      axios.get(`http://localhost:3000/api/modules/${moduleId}/overview`),
      axios.get(`http://localhost:3000/api/modules/${moduleId}/syllabus`)
    ])
    .then(axios.spread((overviewRes, syllabusRes) => {
      // Update list with overview and syllabus for the module
      const updatedList = list.map(item => {
        if (item.id === moduleId) {
          return {
            ...item,
            overview: overviewRes.data.overview,
            syllabus: syllabusRes.data.syllabus
          };
        }
        return item;
      });
      setList(updatedList);
    }))
    .catch(error => {
      console.error('Error fetching module details:', error);
    });
  };

  return (
    <div className='container' id='Modules'>
      <div className='leftbox'>
        <LeftBar tabNav="Modules" />
      </div>
      <div className='maincenter'>
        <div className='topline'>
          <div className='topMain'>
            <h2 className='gFont'>Module</h2>
            <div className='fn-clear'>
              {list.map((ele, i) => {
                return (
                  <Button
                    key={i}
                    size="small"
                    variant="contained"
                    color="primary"
                    onClick={() => { handleButtonClick(i) }}
                  >
                    ID:{ele.id}
                  </Button>
                );
              })}
            </div>
          </div>
        </div>
        <div className='mainList'>
          <h2 className='gFont'>Module List</h2>
          <TableContainer component={Paper} className='tablebox' ref={tableBoxRef}>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>Module</TableCell>
                  <TableCell align="center">Credits</TableCell>
                  <TableCell align="center"></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {list.map((item, i) => (
                  <Fragment key={i}>
                    <TableRow id={`line${i}`}>
                      <TableCell component="th" scope="row" ref={refsdata.current[i]}>
                        {item.module}
                      </TableCell>
                      <TableCell align="center">{item.credits}</TableCell>
                      <TableCell align="center">
                        <IconButton aria-label="expand row" size="small" onClick={() => {
                          let updatedList = [...list];
                          updatedList[i].open = !updatedList[i].open;
                          setList(updatedList);
                        }}>
                          {item.open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                        </IconButton>
                      </TableCell>
                    </TableRow>
                    <TableRow>
                      <TableCell colSpan={3}>
                        <Collapse in={item.open} timeout="auto" unmountOnExit>
                          <Box margin={1}>
                            <Typography variant="subtitle1" display="block">
                              Overview
                            </Typography>
                            <Typography variant="body1" display="block">
                              {item.overview}
                            </Typography>
                            <Divider style={{ margin: '32px 0' }} />
                            <Typography variant="subtitle1" display="block">
                              Syllabus
                            </Typography>
                            <Typography variant="body1" display="block" style={{ whiteSpace: 'pre-line' }}>
                              {item.syllabus}
                            </Typography>
                          </Box>
                        </Collapse>
                      </TableCell>
                    </TableRow>
                  </Fragment>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </div>
      </div>
    </div>
  );
}

export default Modules;
