import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './leftbar.css';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import logo from '../../assets//logo.png';

function LeftBar(props) {
  const [selectedIndex, setSelectedIndex] = useState(props.tabNav);
  const [isSidebarVisible, setSidebarVisible] = useState(true);

  const toggleSidebar = () => {
    setSidebarVisible(!isSidebarVisible);
  };

  useEffect(() => {
    console.log(props.tabNav);
    setSelectedIndex(props.tabNav);
  }, [props.tabNav]);

  return (
    <div id='leftbar'>
      <button onClick={toggleSidebar} className={`toggle-button ${isSidebarVisible ? 'visible' : 'hidden'}`}>â˜°</button>
      {isSidebarVisible && (
        <>
          <Card variant="outlined" className='topbox'>
            <CardContent>
              <img className='logo' src={logo} alt="logo" />
              <Typography className='details' gutterBottom>
                Student Name:xxx
              </Typography>
              <Typography className='details' gutterBottom>
                Student ID:xxx
              </Typography>
              <Typography className='details'>
                Year:xxx
              </Typography>
            </CardContent>
          </Card>

          <Divider />
          <List className='navbox'>
            {['Programme', 'Modules', 'CourseWork', 'Exam', 'Absence', 'MyTutor', 'MyPrograme'].map((item) => (
              <React.Fragment key={item}>
                <ListItem button component={Link} className={selectedIndex === item ? 'sure' : ''} to={`/${item}`}>
                  <i className={`iconfont icon-${item.toLowerCase()}`}></i>
                  {item}
                </ListItem>
                <Divider />
              </React.Fragment>
            ))}
          </List>
        </>
      )}
    </div>
  );
}

export default LeftBar;
