import React, { useState, useEffect, Fragment, useRef } from 'react';
import LeftBar from './../../component/leftbar/leftbar'
import {
  Link, useNavigate
} from 'react-router-dom';
import './coursework.css'
import { makeStyles } from '@material-ui/core/styles';
import {Button, TextField, Divider
} from '@material-ui/core';
function CourseWork (props) {
  const [msg, setmsg] = useState('');/* 提示的文案信息 */
  const [list, setlist] = useState([1, 2, 3, 4, 5, 6])
  const navigate = useNavigate();
  /* 上传的文件 */
  const [file, setFile] = useState(null);
  /* 输入的textarea留言文本 */
  const [text, setText] = useState('');
  useEffect(() => {
  }, []);

  /* 文件上传提交的表单 */
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/zip') {
      setFile(selectedFile);
    } else {
      alert('请上传zip压缩包文件');
    }
  };

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = () => {
    // 在这里可以处理提交表单的逻辑
    console.log('文件:', file);
    console.log('文本内容:', text);
  };
  return (
    <div className='container' id='coursework'>
      <div className='leftbox'>
        <LeftBar tabNav="CourseWork" />
      </div>
      <div className='maincenter'>
        <div className='topline'>
          <div className='topMain'>
            <h2 className='gFont'>Module</h2>
            <div className='fn-clear'>
              {list.map((ele, i) => {
                return <Button key={i} size="small" variant="contained" color="primary" onClick={() => {

                }}>ID:1</Button>
              })}

            </div>
          </div>
        </div>
        <Divider />
        <div className='centerbox'>
          <div className='btn-group'>
            <div className='leftbox'>
              <h2 className='gFont'>Module Name</h2>
              <p className='timesline'>
                开始时间：2021-11-12  结束时间：2021-11-12
              </p>
            </div>
            <div className='righttxt'>
              分数：
              <br />
              <span className='gFont'>88.88</span>
            </div>
          </div>
        </div>
        <div className='mainList'>
          <h2 className='gFont'>Homework Detail</h2>
          <div className='txt'>
            xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx
            text description
          </div>
        </div>
        {/* 提交的表单 */}
        <div className='subform' style={{ with: "800px" }}>
          <p className='line'><span>submit File:  </span><input type="file" accept=".zip" onChange={handleFileChange} /></p>
          <br />

          <TextField
            label="请输入内容"
            variant="outlined" fullWidth
            type='text'
            multiline
            minRows={4}
            style={{ margin: 8 }}
            value={text}
            onChange={handleTextChange}
          />
          <br />
          <Button variant="contained" color="secondary" onClick={handleSubmit}>
            Submit
          </Button>
        </div>
      </div>
    </div >
  );
}
export default CourseWork;
