import React, { useState, useEffect } from 'react';
import LeftBar from '../../component/leftbar/leftbar';
import { Button } from '@material-ui/core';
import './exam.css';

function Exam() {
  // 增加一个状态来跟踪选中的模块ID
  const [selectedModuleId, setSelectedModuleId] = useState(1);
  const [list] = useState(Array.from({length: 10}, (_, i) => i + 1)); // 生成1到10的数组

  useEffect(() => {
    // 根据需要添加副作用逻辑
  }, [selectedModuleId]); // 当selectedModuleId变化时，这里可以做一些数据获取或者其他操作

  return (
      <div className='container' id='exam'>
        <div className='leftbox'>
          <LeftBar tabNav="Exam" />
        </div>
        <div className='maincenter'>
          <div className='topline'>
            <div className='topMain'>
              <h2 className='gFont'>Module</h2>
              <div className='fn-clear'>
                {list.map((id) => {
                  return (
                      <Button
                          key={id}
                          size="small"
                          variant="contained"
                          color="primary"
                          onClick={() => setSelectedModuleId(id)} // 更新选中的模块ID
                      >
                        ID:{id}
                      </Button>
                  );
                })}
              </div>
            </div>
          </div>
          <div className='centerbox'>
            <div className='btn-group'>
              <div className='leftbox'>
                <h2 className='gFont'>Module Name{selectedModuleId}</h2> {/* 根据选中的模块ID动态显示模块名 */}
                <p className='timesline'>
                  开始时间：2024-11-12
                  <br />
                  结束时间：2024-11-12
                  <br />
                  地点：xxxx
                  <br />
                  持续时间：2:00:00
                </p>
              </div>
              <div className='righttxt'>
                分数：
                <br />
                <span className='gFont'>88</span>
              </div>
            </div>
          </div>
          <div className='tipbox'>
            <h2 className='gFont'>Tips</h2>
            <p className='desc'>
              xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx
            </p>
          </div>
        </div>
      </div>
  );
}

export default Exam;
