import React, { useState, useEffect, Fragment, useRef, createRef } from 'react';
import axios from 'axios';
import LeftBar from './../../component/leftbar/leftbar'
import { useNavigate } from 'react-router-dom';
import './Modules.css'
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  IconButton,
  Collapse,
  Box,
  Divider,
} from '@material-ui/core';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import Typography from '@material-ui/core/Typography';

function Modules() {
  const [modules, setModules] = useState([]);
  const navigate = useNavigate();
  const refsdata = useRef([]);
  useEffect(() => {
    axios.get('http://localhost:3000/api/modules')
      .then(response => {
        setModules(response.data);
        refsdata.current = response.data.map((_, i) => refsdata.current[i] ?? createRef());
      })
      .catch(error => {
        console.error('Error fetching modules:', error);
      });
  }, []);

  const tableBoxRef = useRef(null);

  const handleButtonClick = (id, index) => {
    axios.get(`http://localhost:3000/api/modules/${id}`)
      .then(response => {
        // Handle response, e.g., display module details
        console.log(response.data);
      })
      .catch(error => {
        console.error('Error fetching module details:', error);
      });

    const rowElement = refsdata.current[index];
    if (rowElement && rowElement.current) {
      tableBoxRef.current.scrollTo({
        top: rowElement.current.offsetTop,
        behavior: 'smooth',
      });
    }
  };

  return (
    <div className='container' id='Modules'>
      <div className='leftbox'>
        <LeftBar tabNav="Modules" />
      </div>
      <div className='maincenter'>
        <div className='topline'>
          <div className='topMain'>
            <h2 className='gFont'>Module</h2>
            <div className='fn-clear'>
              {modules.map((module, i) => {
                return (
                  <Button
                    key={i}
                    size="small"
                    variant="contained"
                    color="primary"
                    onClick={() => { handleButtonClick(module.id, i) }}
                  >
                    ID:{module.id}
                  </Button>
                );
              })}
            </div>
          </div>
        </div>
        <div className='mainList'>
          <h2 className='gFont'>Module List</h2>
          <TableContainer component={Paper} className='tablebox' ref={tableBoxRef}>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>Module</TableCell>
                  <TableCell align="center">Credits</TableCell>
                  <TableCell align="center"></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {modules.map((module, i) => (
                  <Fragment key={i}>
                    <TableRow id={`line${i}`}>
                      <TableCell component="th" scope="row" ref={refsdata.current[i]}>
                        {module.module}
                      </TableCell>
                      <TableCell align="center">{module.credits}</TableCell>
                      <TableCell align="center">
                        <IconButton aria-label="expand row" size="small" onClick={() => { handleButtonClick(module.id, i) }}>
                          {module.open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                        </IconButton>
                      </TableCell>
                    </TableRow>
                    <TableRow>
                      <TableCell colSpan={3}>
                        <Collapse in={module.open} timeout="auto" unmountOnExit>
                          <Box margin={1}>
                            <Typography variant="subtitle1" display="block">
                              Overview
                            </Typography>
                            <Typography variant="body1" display="block">
                              {/* Render module overview here */}
                            </Typography>
                            <Divider style={{ margin: '32px 0' }} />
                            <Typography variant="subtitle1" display="block">
                              Syllabus
                            </Typography>
                            <Typography variant="body1" display="block" style={{ whiteSpace: 'pre-line' }}>
                              {/* Render module syllabus here */}
                            </Typography>
                          </Box>
                        </Collapse>
                      </TableCell>
                    </TableRow>
                  </Fragment>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </div>
      </div>
    </div>
  );
}

export default Modules;
